import React,{useEffect,useState} from 'react'
import { renderMatches, useNavigate } from 'react-router'
import AxiosRequest from '../../axios'
import { DecodeToken } from '../token_decode'


const Notifications = () => {

  const [notifications,setNotifications] = useState([])
  const [activity,setActivity] = useState([])

  const user_id = DecodeToken(localStorage.getItem("access_token"))

  const RetrieveNotifications = async() => {
    const notf = await AxiosRequest.get(`/chat/notifications/${user_id}`)
              .then((res) => {
                console.log(res.data);
                setNotifications(res.data);

              })
              .catch((err) => {console.log(err.data)})
  }


  const DeleteNotofications = async() => {
    const delNot = await AxiosRequest.delete('/shareme/notifications/${user_id}')
                  .then((res) =>{
                    console.log(res.data)
                    RetrieveNotifications();
                  })
                  .catch((err) => {console.log(err.data)})
  }




  useEffect(() => {
    RetrieveNotifications();
  }, [notifications])

  const RenderNotifications = notifications.map((each) => {
    return(
      <div className='bg-white rounded-lg shadow-lg p-11 text-center font-medium text-gray-500'>
        {each.title}
        {each.sender_data}
        {each.sender_name}

      </div>
    );
  })

  const RenderActivity = activity.map((each) => {
    return(
      <div className='bg-white rounded-lg shadow-lg p-11 text-center font-medium text-gray-500'>
        {each.title}
        {each.sender_data}
        {each.sender_name}

      </div>
    );
  })




  return (

    <div>Notifications
      <div className='grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-2'>
      <div className='flex flex-col justify-center items-center'>
      {RenderActivity}
      </div>

      <div className='flex flex-col justify-center items-center'>
      {RenderNotifications}
      </div>

      </div>
    </div>
  )
}

export default Notifications





const DeleteActivity = async(act_id) => {
  const delNot = await AxiosRequest.delete('/shareme/notifications/${user_id}/activity/${act_id}')
                .then((res) =>{
                  console.log(res.data)
                  RetrieveActivity();
                })
                .catch((err) => {console.log(err.data)})
}

const RetrieveActivity = async() => {
  const act = await AxiosRequest.get(`/shareme/notifications/${user_id}/activity`)
            .then((res) => {
              console.log(res.data);
              setActivity(res.data);

            })
            .catch((err) => {console.log(err.data)})

}
