import React,{useState,useEffect} from 'react'
import { useNavigate } from 'react-router';
import AxiosRequest from '../../../axios';
import { DecodeToken } from '../../token_decode';



const RecommendedProducts = (props) => {
    console.log(props)
    const postData = props

    const [recommendedtProducts,setRecommendedProducts]  = useState([])
    const user_id = DecodeToken(localStorage.getItem("access_token")).user_id

    const getProductRecommendation = async() => {

        const dt = postData.category +"/"+postData.id
        const prd = await AxiosRequest.get(`/shareme/product/single_prd_recommend/${dt}`)
                    .then((res) => {console.log(res.data)
                        setRecommendedProducts(res.data)
                    })
                    .catch((err) => {console.log(err.data)})


    }

    const RenderRecommendedProducts = recommendedtProducts.map((each) => {
        console.log(each)
        return(
            <div className='bg-white, shadow-lg, rounded-md'>
                <p className='text-xl font-light'>{each.title}</p>
                <p className='text-xl font-light'>{each.price}</p>

            </div>

        );

    })


    useEffect(() => {
        RecommendedProducts();
    }, [])

    return (
        <div>             
            <p className='mt-20 text-2xl'>Recommended Products</p>
            {RenderRecommendedProducts}
        </div>
  )
}

export default RecommendedProducts
