import React,{useEffect,useState} from 'react'
import { renderMatches, useNavigate } from 'react-router'
import AxiosRequest from '../../axios'
import { DecodeToken } from '../token_decode'


const Notifications = () => {

  const [notifications,setNotifications] = useState([])


  const user_id = DecodeToken(localStorage.getItem("access_token")).user_id 

  const RetrieveNotifications = async() => {
    const notf = await AxiosRequest.get(`/chat/notifications/${user_id}`)
              .then((res) => {
                console.log(res.data);
                setNotifications(res.data);

              })
              .catch((err) => {console.log(err.data)})
  }


  const DeleteNotofications = async() => {
    const delNot = await AxiosRequest.delete('/chat/notifications/${user_id}')
                  .then((res) =>{
                    console.log(res.data)
                    RetrieveNotifications();
                  })
                  .catch((err) => {console.log(err.data)})
  }




  useEffect(() => {
    RetrieveNotifications();
  }, [notifications])

  const RenderNotifications = notifications.map((each) => {
    return(

      <div className='bg-white rounded-lg shadow-lg p-11 text-center font-medium text-gray-500 my-6'>

        <p > {each.sender_data}</p>



      </div>

    );
  })


  return (

    <div>
      <h1 className='display-6'>
      Notifications
      </h1>
      <div className='grid grid-cols-1 w-1/3 text-center'>



      {RenderNotifications}


      </div>
    </div>
  )
}

export default Notifications
